// å›æº¯è®°å¿†æ³•class Solution &#123;    private Set&lt;String&gt; set;    private int[] memo;    public boolean wordBreak(String s, List&lt;String&gt; wordDict) &#123;        memo = new int[s.length()];        set = new HashSet&lt;&gt;(wordDict);        return backtracking(s, 0);    &#125;    public boolean backtracking(String s, int startIndex) &#123;        // System.out.println(startIndex);        if (startIndex == s.length()) &#123;            return true;        &#125;        if (memo[startIndex] == -1) &#123;            return false;        &#125;        for (int i = startIndex; i &lt; s.length(); i++) &#123;            String sub = s.substring(startIndex, i + 1);		    // æ‹†åˆ†å‡ºæ¥çš„å•è¯æ— æ³•åŒ¹é…            if (!set.contains(sub)) &#123;                continue;                            &#125;            boolean res = backtracking(s, i + 1);            if (res) return true;        &#125;        // è¿™é‡Œæ˜¯å…³é”®ï¼Œæ‰¾éäº†startIndex~s.length()ä¹Ÿæ²¡èƒ½å®Œå…¨åŒ¹é…ï¼Œæ ‡è®°ä»startIndexå¼€å§‹ä¸èƒ½æ‰¾åˆ°        memo[startIndex] = -1;        return false;    &#125;&#125;
